import React, { Component } from "react";

export default class RoomProvider extends Component {
  state = {
    greeting: "hello",
    name: "jonh"
  };

  render() {
    return (
      <RoomContext.Provider value={{}}>
        {this.props.children}
      </RoomContext.Provider>
    );
  }
}

