import React from "react";
import RoomsFilter from "./RoomsFilter";
import RoomList from "./RoomList";
import {
  RoomConsumer
} from "../contex";
import Loading from "../components/Loading";
import {
  withRoomConsumer
} from "../contex"

export default function RoomContainer() {
  return ( <
    >
    <
    RoomConsumer > {
      value => {
        const {
          loading,
          sortedRooms,
          rooms
        } = value;
        if (loading) {
          return <Loading / > ;
        }
        return ( <
          div >
          Hello from rooms container <
          RoomsFilter rooms = {
            rooms
          }
          /> <
          RoomList rooms = {
            sortedRooms
          }
          /> <
          /div>
        );
      }
    } <
    /RoomConsumer> <
    />
  );
}
