import React from "react";
import { useContex } from "react";
import { RoomContex } from "../contex";
import { Title } from "../components/Loading";
//get all unique values
const getUniqie = (items, value) => {
  return [...new Set(item.map(item => item[value]))];
};

export default function RoomsFilter({ rooms }) {
  const context = useContex(RoomContex);
  const {
    handleChange,
    type,
    capacity,
    price,
    minPrice,
    maxPrice,
    minSize,
    maxSize,
    breakfast,
    pets
  } = context;
  let types = getUniqie(rooms, "type");
  return (
    <section className="filter-container">
      <Title title="search rooms"></Title>
      <form action="" className="filter-form">
        {/* select type */}
        <div className="form-group">
          <label htmlFor="type" onChange={handleChange}>
            room type
          </label>
          <select
            name="type"
            id="type"
            value={type}
            className="form-control"
          ></select>
        </div>
      </form>
    </section>
  );
}
